import React, { useEffect } from 'react';
import { FiChevronLeft } from 'react-icons/fi';
import {useRouteMatch, Link} from 'react-router-dom';
import {FiChevronRight} from 'react-icons/fi';
/*1*/ import api from '../../services/api';

import logoImg from '../../assets/images/logotipo.svg';
import {Header, ReposityInfo, Issues} from './styles';

interface RepositoryParams {
  repository: string
}

const Reposity: React.FC = () => {
  const { params } = useRouteMatch<RepositoryParams>();

  /*2*/ //Para acessar as issues do repositório precisa, acessar no link do github a opção issue
  useEffect(() => {
    api.get(`repos/${params.repository}`).then(response => {
      console.log(response.data);
    })
  }, []);

  return (
    <>
    <Header>
    <img src={logoImg} alt="Github Explorer" />
    <Link to="/">
      <FiChevronLeft size={16} />
      Voltar
    </Link>
    </Header>
    <ReposityInfo>
      <header>
          <img src="https://avatars1.githubusercontent.com/u/37426078?s=460&u=88c91ea9279079b493564ad2d08e979471e67df1&v=4" alt="Mercia Keila"/>
          <div><strong>{params.repository}</strong>
      <p>Descrição do repo</p></div>
      </header>
      <ul>
        <li>
          <strong>1808</strong>
          <span>Stars </span>
      </li>
      <li>
          <strong>48</strong>
          <span>Forks</span>
      </li>
      <li>
          <strong>67</strong>
          <span>Issues abertas</span>
      </li>
      </ul>
    </ReposityInfo>
    <Issues>
      <Link key={repository.full_name} to={`/repositories/${repository.full_name}`}>
        <img src={repository.owner.avatar_url} alt={repository.owner.login}/>
        <div>
         <strong>{repository.full_name}</strong>
        <p>{repository.description}</p>
        </div>
        <FiChevronRight size={20}/>
      </Link>
    </Issues>
    </>
  )
}

export default Reposity;
