import React, {useState, FormEvent} from 'react';
import {Title, Form, Repositories} from './styles';
import {FiChevronRight} from 'react-icons/fi';
import api from '../../services/api';
import logoImg from '../../assets/images/logotipo.svg';
//import Repository from '../Repository';

const Dashboard: React.FC = () => {
  const [newRepo, setNewRepo] = useState(''); // Estado para armazenar o valor do input
  const [repositories, setRepositories] = useState([])//Vai armazenar os repositórios usando o useState

  function hundleAddRepository(event): void {
    console.log(newRepo);
    //Adição de um novo repositŕoio
    //Consumir a API do Github
    //Salvar novo respositório no estado


  }

  /* No input, o valor que vai receber é o value={newRepo}
  Quando o usuário altera o valor desse input, recebe um evento onChange={(e) => setNewRepo(e.target.value)}
  */

  return (
    <>
    <img src={logoImg} alt="Github Explorer" />
    <Title>Explore repositórios no Github</Title>

    <Form onSubmit={hundleAddRepository}>
      <input placeholder="Digite o nome do repositório" value={newRepo} onChange={(e) => setNewRepo(e.target.value)}/>
      <button type="submit">
        Pesquisar
      </button>
    </Form>

    <Repositories>
      <a href="teste">
        <img src="https://avatars1.githubusercontent.com/u/37426078?s=460&u=88c91ea9279079b493564ad2d08e979471e67df1&v=4" alt="Mercia Keila"/>

        <div>
          <strong>Docs com Gatsby</strong>
          <p>gatsby-starter-rocket-docs</p>
        </div>
        <FiChevronRight size={20}/>
      </a>

      <a href="teste">
        <img src="https://avatars1.githubusercontent.com/u/37426078?s=460&u=88c91ea9279079b493564ad2d08e979471e67df1&v=4" alt="Mercia Keila"/>

        <div>
          <strong>Docs com Gatsby</strong>
          <p>gatsby-starter-rocket-docs</p>
        </div>
        <FiChevronRight size={20}/>
      </a>

      <a href="teste">
        <img src="https://avatars1.githubusercontent.com/u/37426078?s=460&u=88c91ea9279079b493564ad2d08e979471e67df1&v=4" alt="Mercia Keila"/>

        <div>
          <strong>Docs com Gatsby</strong>
          <p>gatsby-starter-rocket-docs</p>
        </div>
        <FiChevronRight size={20}/>
      </a>
    </Repositories>
    </>
  )
}

export default Dashboard;
