import {createGlobalStyle} from 'styled-components';


export default createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    outline: 0;
    box-sizing: border-box;
  }

  body {
    background: #f0f0f5;
    -webkit-font-smooth: antialiased;
  }

  border-style, input, button {
    font-size: 16px;
    font-family: 'Roboto', sans-serif;
  }
`
