    }
  }

  button  {
    width: 210px;
    height: 70px;
    background: #04d361;
    border-radius: 0px 5px 5px 0px;
    color: #FFF;
    border: none;
    font-weight: bold;
    transition: background-color 0.2s;

    &:hover {
      background:  ${shade(0.2, '#04d361')};
    }
  }
`
/*15º*/
export const Error = styled.span`
  display: block;
  color: #c53030;
  margin-top: 10px;
`

export const Repositories = styled.div`
  margin-top: 80px;
